#number of pseudo who start with num
def pseudo_num(tab_name)
	number = 0
	tab = tab_name.length

	tab.times do |i|
		vao = tab_name[i]
		handle = 0
		vao.length.times do |j|
			case vao[j]
			when "0".."9"
				handle = 1
			end
		end
		if handle == 1
			number += 1
		end
	end

puts number
end

#number of the journalist who pseudo content aude
def number_aude(tab_name)
	number = 0
	tab = tab_name.length

	tab.times do |i|
		vao = tab_name[i]
		handle = 0
		vao.length.times do |j|
			if vao[j] == "a" || vao[j] == "A"
				if vao[j+1] == "u"
					if vao[j+2] == "d"
						if vao[j+3] == "e"
							handle = 1
						end
					end
				end
			end

			
		end
		if handle == 1
			number += 1
		end
	end
	puts number
end

