#50 handle le plus courts
def pseudo_short(tab_name)
	tab = tab_name.length - 1
	essai = 0
	while essai == 0
		essai = 1
		for i in 0...tab
			if tab_name[i].length > tab_name[i+1].length
			tab_name[i],tab_name[i+1] = tab_name[i+1],tab_name[i]
			essai = 0
			end
		end
	end
	puts tab_name[0..50]
end

